!(function () {
  let a = document.querySelector(".burger"),
    c = document.querySelector(".header__nav"),
    b = document.querySelector(".header__nav-close");
  a.addEventListener("click", () => {
    c.classList.add("header__nav-active");
  }),
    b.addEventListener("click", () => {
      c.classList.remove("header__nav-active");
    });
})();
